import React, { Component } from 'react';
import { Button } from 'react-bootstrap';
import 'bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap/dist/js/bootstrap.js';

export default class Upload extends Component {
    // Get a reference to the database service

    state = {
      selectedFile: null,
    }

    fileSelectedHandler  = event => {
      this.setState({
        selectedFile: event.target.files[0],
      })
    }

    ontrueHandler = event => {
      console.log(this.state.selectedFile)
    }

    handleClick(ob){
        console.log(ob)
    }


    render(){
  return (
    <div >
      <div class="dropdown">
      <div class="form-group">
        <label for="exampleInputPassword1">Price</label>
        <input type="text" class="form-control" id="exampleInputPassword1" placeholder="Password"/>
      </div>
      <div class="form-group">
        <label for="exampleInputPassword1">Quantity</label>
        <input type="text" class="form-control" id="exampleInputPassword1" placeholder="Password"/>
      </div>
      <button class="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
        Catergory
      </button>
      <div class="dropdown-menu" aria-labelledby="dropdownMenuButton">
        <a class="dropdown-item" onClick={() => this.handleClick("fruit")}>Fruit</a>
        <a class="dropdown-item" onClick={() => this.handleClick("veg")}href="#">Veg</a>
        <a class="dropdown-item" onClick={() => this.handleClick("drinks")} href="#">Drinks</a>
      </div>
    </div>
      <input type="file" onChange={this.fileSelectedHandler}/>
      <Button onClick={this.ontrueHandler}>Upload</Button>
      

    </div>
  );
}}
