import React, { Component } from 'react';
import { Button } from 'react-bootstrap';
import 'bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap/dist/js/bootstrap.js';

class Upload extends Component {
    // Get a reference to the database service
    constructor(props){
      super(props)
      this.onChangeDescription=this.onChangeDescription.bind(this);
      this.onChangeQuantity=this.onChangeQuantity.bind(this);
      this.onChangePicture=this.onChangePicture.bind(this);
      this.onSubmitForm=this.onSubmitForm.bind(this);

    this.state = {
      descriptionx: "",
      Quantity:"",
      picture:null
    };
    }

//    fileSelectedHandler  = event => {
 //     this.setState({
  //      selectedFile: event.target.files[0],
   //   })
    //}

    onChangeDescription(e){
      this.setState({
        descriptionx : e.target.value
      });

    }

    onChangeQuantity(e){
      this.setState({
        Quantity : e.target.value
      });

    }

    
    onChangePicture(e){
      this.setState({
        Quantity : e.target.value[0]
      });

    }


    onSubmitForm() { 


      //submit logic lol
      console.log("Description  "+this.state.descriptionx)
      console.log("Quanity  " +this.state.Quantity)
      console.log("Picture  " +this.state.picture)

      this.setState({
          descriptionx : "",
          Quantity : "",
          picture : null
      });

    }



    render(){
  return (
    <div width="40%">
      <div class="dropdown">
      <form>
      <div class="form-group">
        <label for="F1">Description</label>
        <input style={{width:"30%"}}type="text" class="form-control" id="desccrriptiomn" placeholder="description" value={this.state.descriptionx} onChange={this.onChangeDescription}/>
        <label for="F2">Quantity</label>
        <input style={{width:"30%"}}type="text" class="form-control" id="desccrriptiomn" placeholder="Quantity" value={this.state.Quantity} onChange={this.onChangeQuantity}/>
        <label for="exampleFormControlFile1">Upload Item</label>
        <input type="file" class="form-control-file" id="exampleFormControlFile1" value={this.state.picture} onChange={this.onChangePicture}/>  
      </div>
     




      <Button onClick={this.onSubmitForm}>Upload</Button>
    </form>
      
    </div>
    </div>
    
  );
}}

export default Upload;
