import React, { Component } from 'react';
import fire from '../config/fire';
import { Button } from 'react-bootstrap';
import EXIF from "exif-js";

export default class Upload extends Component {
    // Get a reference to the database service

    
    state = {
      selectedFile: null,
      fileExists: null,
    }

    
    

    fileSelectedHandler  = event => {
      this.setState({
        selectedFile: event.target.files[0],
        fileExists: true
      })
    }

    ontruehandler = event =>
    {
      if (this.state.selectedFile){
        this.onUploadHandler()
      }
      else
      {
        this.setState({
          fileExists:true
        })
      }
    
    }


    onUploadHandler = event => {

      function convertDMStoDD(GPSLat,GPSLong) { 
        console.log("TEST LAT  : "+GPSLat)
        var long = GPSLong[0] + (GPSLong[1]/60) + (GPSLong[2]/3600);
        var Lat1 = GPSLat[0] + (GPSLat[1]/60) + (GPSLat[2]/3600);
        return [Lat1,long];
        
      }
     
      
      
      //var database = fire.database();
      var image = this.state.selectedFile;
      var filename = this.state.selectedFile.name;
      var user = fire.auth().currentUser;
      //console.log("User Token: " + user.uid);
      var min = new Date().getMinutes();
      filename = "M" + min + filename;
      var storageRef = fire.storage().ref('/pictures/' + filename);
      var uploadTask = storageRef.put(this.state.selectedFile);
     
      uploadTask.on('state_changed', function(snapshot){
        // Observe state change events such as progress, pause, and resume
        // Get task progress, including the number of bytes uploaded and the total number of bytes to be uploaded
        var progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
        console.log('Upload is ' + progress + '% done');
        
      }, function(error) {
        console.log(error)
        // Handle unsuccessful uploads
        console.log('Upload was unsuccessful')
      }, function() {
          uploadTask.snapshot.ref.getDownloadURL().then(function(downloadURL) {
          //consoleconsoleconsoleconsole.log('File available at', downloadURL);
          //console.log("Name: " +filename)

          if (image) {
            EXIF.getData(image, function() {
            //console.log(image.exifdata);
            var x = convertDMStoDD(image.exifdata.GPSLatitude,image.exifdata.GPSLongitude);
            var newPostKey = fire.database().ref().child(user.uid).push().key;
            fire.database().ref('users/'+user.uid+"/"+newPostKey+"/").set({
              imageName: filename,
              ImageUrl: downloadURL,
              GPSLat: x[0],
              GPSLong: x[1]
            });
            
            
            });
          }else{
            console.log("get ur shit together")
          }

        });
      });
 

      //fire.database().ref('test-962dc/' + "test").set({
      ///=  imageName: this.state.selectedFile.name,
      //});
// Listen for state changes, errors, and completion of the upload.


    }
      //console.log("uploaded")
      //console.log(this.state.selectedFile.name)


    render(){
  return (
    <div >
      <input type="file" onChange={this.fileSelectedHandler}/>
      <Button onClick={this.ontruehandler}>Upload</Button>
      
      {this.state.fileExists
        ? <p>File exists</p>
        : <p>file doesnt exist</p>
      }

    </div>
  );
}}
