import React, { Component } from 'react';
import { Button } from 'react-bootstrap';
import 'bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap/dist/js/bootstrap.js';

export default class Upload extends Component {
    // Get a reference to the database service

    state = {
      description:"",
      Quantity:"",
      selectedFile: null,
    }

    fileSelectedHandler  = event => {
      this.setState({
        selectedFile: event.target.files[0],
      })
    }

    onChangeDescription(e){
      this.setState({
        description:e.target.value
      });
    }

    onChangeQuantity(e){
      this.setState({
        Quantity:e.target.value
      });
    }

    onSubmitForm(e) { 
      e.preventDefault();

      //submit logic lol
      console.log("Form submitted: ")
      console.log("Form submitted: " + this.state.description)



    }



    render(){
  return (
    <div width="40%">
      <div class="dropdown">

      <div class="form-group">
        <label for="exampleInputPassword1">Description</label>
        <input style={{width:"30%"}}type="text" class="form-control" id="exampleInputPassword1" placeholder="description"/>
      </div>
      <div class="form-group">
        <label for="exampleInputPassword1">Quantity</label>
        <input style={{width:"30%"}}type="text" class="form-control" id="exampleInputPassword1" placeholder="quanity"/>
      </div>
      <button class="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
        Catergory
      </button>
      <div class="dropdown-menu" aria-labelledby="dropdownMenuButton">
        <a class="dropdown-item" onClick={() => this.handleClick("Jewlery")}>Fruit</a>
        <a class="dropdown-item" onClick={() => this.handleClick("Memorbialia")}href="#">Veg</a>
        <a class="dropdown-item" onClick={() => this.handleClick("colectables")} href="#">Drinks</a>
      </div>
    </div>
      <input style={{paddingTop:"20px"}} type="file" onChange={this.fileSelectedHandler}/>
      <Button onClick={this.onSubmitForm}>Upload</Button>
      

    </div>
  );
}}
