import React, { Component } from 'react';
import { Button } from 'react-bootstrap';
import axios from 'axios';

class Weather extends Component{
  
    constructor(props){
        super(props)
        this.onChangeLocation=this.onChangeLocation.bind(this);
        this.onSubmit=this.onSubmit.bind(this);
  
      this.state = {
        city: "",
        data:[]
      };
      }

      onChangeLocation(e){
        this.setState({
          city : e.target.value
        });
  
      }

      onSubmit(e) {
        e.preventDefault();
        this.getWeather();
      }
  

  getWeather = async () => 
  {
      const api = await fetch("http://api.openweathermap.org/data/2.5/weather?q="+this.state.city+",ie&appid=5bc1af2f0ece90619d3d7525233411a8")
      const response = await api.json();
      this.setState({
        data : response
      });

      console.log(JSON.stringify(response))
  }


  render() {
    return (
        <div>
        <div  style={{ width:"40%",  border: '2px solid black' ,  borderRadius: "25px", margin:"auto"}}>
        <div style={{margin:"10%"}}>
        <form>
        <h3>Select city</h3>
        <div class="form-group">
        <label form="F1">City</label>
          <input style={{width:"90%"}}type="text" className="form-control" id="name" placeholder="city" value={this.state.city} onChange={this.onChangeLocation}/>
         

        </div>
       
  
  
  
  
        <Button onClick={this.onSubmit}>Upload</Button>
      </form>
      </div>
      </div>

        <div>
            <p>{data.map(data => <div>{data.main}</div>)}</p>
        </div>
      </div>
    )
  }
}

export default Weather
