import React, { Component } from 'react';
import { Button } from 'react-bootstrap';
import 'bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap/dist/js/bootstrap.js';

export default class Upload extends Component {
    // Get a reference to the database service
    constructor(props){
      super(props)
      this.onChangeDescription=this.onChangeDescription.bind(this);

    this.state = {
      description:""

    };
    }

//    fileSelectedHandler  = event => {
 //     this.setState({
  //      selectedFile: event.target.files[0],
   //   })
    //}

    onChangeDescription(e){
      this.setState({
        description:e.target.value
      });
    }


    onSubmitForm(e) { 
      e.preventDefault();

      //submit logic lol
      console.log("Form submitted: ")
      console.log(this.state.description)



    }



    render(){
  return (
    <div width="40%">
      <div class="dropdown">

      <div class="form-group">
        <label for="exampleInputPassword1">Description</label>
        <input style={{width:"30%"}}type="text" class="form-control" id="desccrriptiomn" placeholder="description" value={this.state.description} onChange={this.onChangeDescription}/>
      </div>

      <Button onClick={this.onSubmitForm}>Upload</Button>
      
    </div>
    </div>
  );
}}
