import React, { Component } from 'react';

import axios from 'axios';

class Weather extends Component{
  
constructor(props)
{
    super(props)
    this.getWeather();
    this.state = {
        currentCity: "",
        weather:null
    }
}

  getWeather = async () => 
  {
      const api = await fetch("http://api.openweathermap.org/data/2.5/weather?q=Dublin,ie&appid=5bc1af2f0ece90619d3d7525233411a8")
      const response = await api.json();
      this.setState({
          weather:response
      })
      console.log(JSON.stringify(response))
  }


  render() {
    return (
      <ul>
          <p>{this.weather.main}</p>
    
      </ul>
    )
  }
}

export default Weather
