import React, {Component} from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { Button } from 'react-bootstrap';

const Todo = props => (
    // ccreate table prop to display items, last one needs image url -> issue is size of picture
    <tr>
        <td>{props.item.item_name}</td>
        <td>{props.item.item_description}</td>
        <td>{props.item.item_quantity}</td>
        <td style={{width:"40%"}}><img style={{width:"40%"}} src="https://images.unsplash.com/photo-1494253109108-2e30c049369b?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1350&q=80" alt="Flowers in Chania"/> </td>
        <td ><button type="button" class="btn btn-info btn-lg" data-toggle="modal" data-target="#myModal">Open Modal</button></td>
    </tr>
)

export default class TodosList extends Component {

    constructor(props) {
        super(props);
        this.state = {item: []};
    }

    componentDidMount() {
        axios.get('http://localhost:4000/item/')
            .then(response => {
                this.setState({item: response.data});
                console.log(response)
            })
            .catch(function (error) {
                console.log(error);
            })
    }

    todoList() {
        return this.state.item.map(function(currentTodo, i) {
            return <Todo item={currentTodo} key={i} />;
        });
    }

    render() {
        return (
            <div>

<button type="button" class="btn btn-info btn-lg" data-toggle="modal" data-target="#myModal">Open Modal</button>
<div class="modal fade" id="myModal" role="dialog">
    <div class="modal-dialog">
    

      <div class="modal-content">
        <div class="modal-header">
          <button type="button" class="close" data-dismiss="modal">&times;</button>
          <h4 class="modal-title">Modal Header</h4>
        </div>
        <div class="modal-body">
          <p>Some text in the modal.</p>
        </div>
        <div class="modal-footer">
          <button type="button" class="btn btn-default" data-dismiss="modal">Close</button>
        </div>
      </div>
      
    </div>
  </div>
      
            </div>
        )
    }
}
