import Home from "./Pages/Home"
import firebase from "./cofigFile/firebase"


class App extends Component{

    constructor (props){
      super(props);
      this.state={
        user:{},
      }
    }

    componentDidMount(){
        this.authListener();
      }

    authListener() {
      firebase.auth().onAuthStateChanged((user) =>{
        if (user) {
          console.log(user)
          this.setState({user});
          //localStorage.setItem("user", user.uid);
        }
        else {
          this.setState({user : null});
          console.log("not signed in")
          //localStorage.removeItem("user");
        }
      });
    }

    test(){
      console.log("TEST");
    }


  render() {
    return (

        <div style={{color: "blue", width:"50%" , backgroundColor:"black", margin:"auto",height:"100vh"}} className="App">
          {this.test()}
          <Home/>
        </div>
    );
  }
}


export default App;
