import React, {Component} from "react";
import { Button } from 'react-bootstrap';

import axios from "axios"

function onChangeName(e) {
    this.setState({
        item_name: e.target.value
    });
}


function afterRain(props) {
    return (
    <div> 
          <div>
                                        <div  style={{ width:"40%",  border: '2px solid black' ,  borderRadius: "25px"}}>
                    <div style={{margin:"10%"}}>
                    <form>
                    <div class="form-group">
                    <label form="F1">Name</label>
                        <input style={{width:"90%"}}type="text" className="form-control" id="name"  value={props.item_name} />
                    
           
                    </div>
                    




                    <Button >Upload</Button>
                    </form>
                    </div>
                    </div>
          </div>
    </div>
    )
}

export default class edit extends Component {
    
    
    constructor(props){
        super(props)

        this.state = { 
            item_name:"",
            item_descriptionx:"",
            item_quantity:"",
            item_url:""
        }
    }
   async componentDidMount (){
        
        await axios.get("http://localhost:4000/item/" + this.props.match.params.id).then(res => {
            this.setState({
                item_name:res.data.item_name,
                item_description:res.data.item_description,
                item_quantity:res.data.item_quantity,
                item_url:res.data.item_url
            })
            console.log(res.data.item_name);
        }).catch(function(err){
            console.log(err)
        })
    }
    

    
    render() {
        return (
            <div>
            {afterRain(this.state)}
          </div>
        )
    }
}
