import React, { Component } from 'react';
import { Button } from 'react-bootstrap';
import 'bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap/dist/js/bootstrap.js';
import axios from "axios";

class Upload extends Component {
    // Get a reference to the database service
    constructor(props){
      super(props)
      this.onChangeName=this.onChangeName.bind(this);
      this.onChangeDescription=this.onChangeDescription.bind(this);
      this.onChangeQuantity=this.onChangeQuantity.bind(this);
      this.onChangePicture=this.onChangePicture.bind(this);
      this.onSubmitForm=this.onSubmitForm.bind(this);

    this.state = {
      name: "",
      descriptionx: "",
      item_quantity:"",
      picture:""
    };
    }

//    fileSelectedHandler  = event => {
 //     this.setState({
  //      selectedFile: event.target.files[0],
   //   })
    //}

    onChangeDescription(e){
      this.setState({
        descriptionx : e.target.value
      });

    }

    onChangeQuantity(e){
      this.setState({
        item_quantity : e.target.value
      });

    }

    onChangeName(e){
      this.setState({
        name : e.target.value
      });

    }

    
    onChangePicture(e){
        this.setState({
          picture : e.target.files[0]
        });

    }


    onSubmitForm() { 


      //submit logic lol
      console.log("Name  "+this.state.name)
      console.log("Description  "+this.state.descriptionx)
      console.log("Quanity  " +this.state.item_quantity)
      console.log("Picture  " +this.state.picture.name)

      //firebase storage to store image return url
      //TODO

      //create new item object to submit
      const newItemObject = {
          item_name: this.state.name,
          item_description: this.state.descriptionx,
          item_quantity: this.state.item_quantity,
          item_url: this.state.picture
      }

      //post moethord from axios to add data
      axios.post("http://localhost:4000/item/add", newItemObject).then(
        res => {
          console.log(res.data);
        }
      )

      this.setState({
          name : "",
          descriptionx : "",
          item_quantity : "",
          picture : ""
      });

    }



    render(){
  return (
    <div  style={{ width:"40%",  border: '2px solid black' ,  borderRadius: "25px", margin:"auto"}}>
      <div style={{margin:"10%"}}>
      <form>
      <h3>New Item</h3>
      <div class="form-group">
      <label form="F1">Name</label>
        <input style={{width:"90%"}}type="text" className="form-control" id="name" placeholder="description" value={this.state.name} onChange={this.onChangeName}/>
       
        <label form="F1">Description</label>
        <input style={{width:"90%"}}type="text" className="form-control" id="desccrriptiomn" placeholder="description" value={this.state.descriptionx} onChange={this.onChangeDescription}/>
        <label form="F2">Quantity</label>
        <input style={{width:"90%"}}type="text" className="form-control" id="desccrriptiomn" placeholder="Quantity" value={this.state.item_quantity} onChange={this.onChangeQuantity}/>
        <label for="exampleFormControlFile1">Upload Item</label>
        <input type="file" class="form-control-file" id="exampleFormControlFile1"  onChange={this.onChangePicture}/>  
      </div>
     




      <Button onClick={this.onSubmitForm}>Upload</Button>
    </form>
    </div>
    </div>
    
  );
}}

export default Upload;
